ont-size:1.2rem;font-weight:700;cursor:pointer;transition:all .3s ease;margin:.5rem}.control-btn:hover:not(:disabled){transform:translateY(-3px);box-shadow:0 8px 20px rgba(123,44,191,.5)}.control-btn:disabled{opacity:.5;cursor:not-allowed}.multiplier-display{background:linear-gradient(145deg,#FFD700,#FFA500);color:#1a0b2e;padding:1rem;border-radius:10px;text-align:center;margin-bottom:1rem;font-size:1.5rem;font-weight:700;border:3px solid #7B2CBF}.game-info{display:grid;grid-template-columns:repeat(3,1fr);gap:1rem;margin-top:2rem}.info-card{background:linear-gradient(145deg,#f8f9fa,#e9ecef);padding:1rem;border-radius:10px;text-align:center;border:2px solid #7B2CBF}.info-value{font-size:1.5rem;font-weight:700;color:#7B2CBF}.info-label{font-size:.9rem;color:#666;margin-top:.5rem}.result-notification{position:fixed;top:50%;left:50%;transform:translate(-50%,-50%) scale(0);background:#fff;padding:3rem;border-radius:20px;box-shadow:0 20px 60px rgba(0,0,0,.5);z-index:1000;text-align:center;min-width:350px;transition:transform .3s ease}.result-notification.show{transform:translate(-50%,-50%) scale(1)}.result-notification.win{border:5px solid #28a745}.result-notification.lose{border:5px solid #dc3545}.overlay{position:fixed;top:0;left:0;width:100%;height:100%;background:rgba(0,0,0,.7);z-index:999;display:none}.overlay.show{display:block}
